                p8txt = "8일뒤 최저 : " + response.data.response.item[0].taMin8 + " / ";
                p9txt = "9일뒤 최저 : " + response.data.response.item[0].taMin9 + " / ";
                p10txt = "10일뒤 최저 : " + response.data.response.item[0].taMin10 + " / ";
                
                p3txt += "3일뒤 최고 : " + response.data.response.item[0].taMax3 + " / ";
                p4txt += "4일뒤 최고 : " + response.data.response.item[0].taMax4 + " / ";
                p5txt += "5일뒤 최고 : " + response.data.response.item[0].taMax5 + " / ";
                p6txt += "6일뒤 최고 : " + response.data.response.item[0].taMax6 + " / ";
                p7txt += "7일뒤 최고 : " + response.data.response.item[0].taMax7 + " / ";
                p8txt += "8일뒤 최고 : " + response.data.response.item[0].taMax8 + " / ";
                p9txt += "9일뒤 최고 : " + response.data.response.item[0].taMax9 + " / ";
                p10txt += "10일뒤 최고 : " + response.data.response.item[0].taMax10 + " / ";
                console.log(response.data.response.item[0].taMin3);
                
            })
            .catch(function (error) {
                console.log(error);
            })
        //중기 육상예보조회
        
        axios.get('http://210.115.227.82:18080/api/weather/midlandfcst' + regId2)
            .then(function (response){
                p3txt += "3일뒤 오전 날씨 : " + response.data.response.item[0].wf3Am + " / ";
                p4txt += "4일뒤 오전 날씨 : " + response.data.response.item[0].wf4Am + " / ";
