                p5txt += "5일뒤 오후 날씨 : " + response.data.response.item[0].wf5Pm + " / ";
                p6txt += "6일뒤 오후 날씨 : " + response.data.response.item[0].wf6Pm + " / ";
                p7txt += "7일뒤 오후 날씨 : " + response.data.response.item[0].wf7Pm + " / ";
                p8txt += "8일뒤 오후 날씨 : " + response.data.response.item[0].wf8 + " / ";
                p9txt += "9일뒤 오후 날씨 : " + response.data.response.item[0].wf9 + " / ";
                p10txt += "10일뒤 오후 날씨 : " + response.data.response.item[0].wf10 + " / ";

                p3txt += "3일뒤 오전 강수 확률 : " + response.data.response.item[0].rnSt3Am + "% / ";
                p4txt += "4일뒤 오전 강수 확률 : " + response.data.response.item[0].rnSt4Am + "% / ";
                p5txt += "5일뒤 오전 강수 확률 : " + response.data.response.item[0].rnSt5Am + "% / ";
                p6txt += "6일뒤 오전 강수 확률 : " + response.data.response.item[0].rnSt6Am + "% / ";
                p7txt += "7일뒤 오전 강수 확률 : " + response.data.response.item[0].rnSt7Am + "% / ";
                p8txt += "8일뒤 오전 강수 확률 : " + response.data.response.item[0].rnSt8 + "% / ";
                p9txt += "9일뒤 오전 강수 확률 : " + response.data.response.item[0].rnSt9 + "% / ";
                p10txt += "10일뒤 오전 강수 확률 : " + response.data.response.item[0].rnSt10 + "% / ";

                p3txt += "3일뒤 오후 강수 확률 : " + response.data.response.item[0].rnSt3Pm + "% / ";
                p4txt += "4일뒤 오후 강수 확률 : " + response.data.response.item[0].rnSt4Pm + "% / ";
                p5txt += "5일뒤 오후 강수 확률 : " + response.data.response.item[0].rnSt5Pm + "% / ";
