    */
    //weatherCast 글자 자르기
    function cutCastQuote(str) {
        let data = str.split('○');
        let rain, temp, sea, weekend;
        rain = data[1];
        temp = data[2];
        sea = data[3];
        weekend = data[4];
        document.getElementById('weather').innerHTML = (rain + "<br>" + temp + "<br>" + sea + "<br>" + weekend + "<br>");
    }

    //기상청 중기전망조회
