//pages/Nav.js
import React from "react";
import './tripNearby.css';
import axios from "axios";



function TripNearby() {
    let currentPage = 1;
    let first;
    getPageNum();
    getPage();

    function getPage() {
        // let type;
        // if (isEvent) {
        //     //console.log("true");
        //     type = "event";
        // } else {
        //     type = "festival";
        // }
        axios.get('http://210.115.227.82:18080/api/trip/list?cnt=8&area=')
            .then(function (response) {
                console.log(response);
            })
            .catch(function (error) {
                //console.log(error);
            })
    }

    //총 페이지 가져오기
    function getPageNum() {

        


        // let type;
        // if (isEvent) {
        //     type = "event";
        //     sessionStorage.setItem("type" , "event");
        // } else {
        //     type = "festival"
        //     sessionStorage.setItem("type" , "festival");
        // }
        axios.get('http://210.115.227.82:18080/api/trip/list/totalpage?cnt=8&area=')
            .then(function (response) {
                console.log(response.data.response.totalPage);

                //totalPage = response.data.response.count;
                //console.log("tot : " + totalPage);

            })
            .catch(function (error) {
                console.log(error);
            })
    }

    //페이지 가져오기. api에서 필요한 데이터를 페이지로 가져옴
    // function getPage() {
    //     let type;
    //     if (isEvent) {
    //         //console.log("true");
    //         type = "event";
    //     } else {
    //         type = "festival";
    //     }
    //     axios.get('http://210.115.227.82:18080/api/' + type + '/list?cnt=10&num=' + currentPage)
    //         .then(function (response) {
    //             responseData = response.data.response;
    //             console.log(responseData);
    //             PrintDataList(response.data.response);
    //         })
    //         .catch(function (error) {
    //             //console.log(error);
    //         })
    // }

    //페이징 버튼 구현
    function btn1_click() {
        //console.log((first) + "clicked!");
        currentPage = first;
        getPage();
    }
    function btn2_click() {
        //console.log((first + 1) + "clicked!");
        currentPage = first + 1;
        getPage();
    }
    function btn3_click() {
        //console.log((first + 2) + "clicked!");
        currentPage = first + 2;
        getPage();
    }
    function btn4_click() {
        //console.log((first + 3) + "clicked!");
        currentPage = first + 3;
        getPage();
    }
    function btn5_click() {
        //console.log((first + 4) + "clicked!");
        currentPage = first + 4;
        getPage();
    }

    function paging(totalPage, currentPage) {
        last = 5;
        if (currentPage < 4) {
            last = 5;
        } else if (currentPage > totalPage - 2) {
            last = totalPage;
        } else {
            last = currentPage + 2;
        }
        first = last - 4;


        //console.log("first = " + first);
        for (let i = 1; i < 6; i++) {
            document.getElementById("btn" + i).innerHTML = (first + i - 1);
        }
    }


    return (
        <div className="Tripcontents">
            <span id = "quote">#내 주변 여행지</span>
            

            
            <div id="pagebtns">
                <button id="btn1" onClick={btn1_click}></button>
                <button id="btn2" onClick={btn2_click}></button>
                <button id="btn3" onClick={btn3_click}></button>
                <button id="btn4" onClick={btn4_click}></button>
                <button id="btn5" onClick={btn5_click}></button>
            </div>
        </div>
    );
}

export default TripNearby;
