//pages/Nav.js
import React from "react";
import axios from "axios";
import "./eventTable.css";

function eventTable() {
    let currentPage = 2;
    let totalPage;
    //data 화면에 출력
    function PrintDataList(data) {
        for (let i = 0; i < 10; i++) {
            document.getElementById("tdName" + i).innerHTML = data.list[i].title;
            document.getElementById("tdStart" + i).innerHTML = data.list[i].startDate;
            document.getElementById("tdEnd" + i).innerHTML = data.list[i].endDate;
        }
        paging(totalPage,currentPage);
    }
    //총 페이지를 가져오는 데이터
    axios.get('http://210.115.227.82:18080/api/event/list/totalpage')
        .then(function (response) {
            //console.log(response.data.response.list[0]);
            //console.log(response.data.response);
            totalPage = response.data.response.count;
        })
        .catch(function (error) {
            console.log(error);
        })

    //pagenum에 적힌 페이지 번호를 불러와서 리스트를 뽑아줌

    axios.get('http://210.115.227.82:18080/api/event/list?cnt=10&num=' + (currentPage-1))
        .then(function (response) {
            //console.log(response.data.response.list[0]);
            PrintDataList(response.data.response);
        })
        .catch(function (error) {
            console.log(error);
        })


    function paging(totalPage,currentPage) {
        const pageGroup = Math.ceil(currentPage / 5);
        console.log("totalpage: " + totalPage);

        let last = 5;
        if(currentPage <4){
            last = 5;
        }else if(currentPage > totalPage-2){
            last = totalPage;
        }else{
            last = currentPage + 2;
        }
        console.log("last = " + last);
    }
    //페이징 함수


    // function paging(totalData, currentPage) {
    //     const dataPerPage = 10;
    //     const pageCount = 5;
    //     const totalPage = Math.ceil(totalData / dataPerPage);
    //     const pageGroup = Math.ceil(currentPage / pageCount);

    //     let last = pageGroup * pageCount;
    //     if( last > totalPage ){
    //         last = totalPage;
    //     }
    //     let first = last - (pageCount -1);
    //     const next = last + 1;
    //     const prev = first - 1;

    //     if(totalPage < 1){
    //         first = last;
    //     }

    //     const pages = $("#pages");
    //     pages.empty();
    //     if(first > 5){
    //         pages.append("<li class = 'pagination'>" + "<a onclick=\"GetTarget(" + (prev) + ");\" style = 'margin-left:2px'>prev</a></li>");
    //     }
    //     for(let j = first ; j <= last ; j++){
    //         if(currentPage === (j)){
    //             pages.append("<li class=\"pagination-item\">" + "<a class='active' onclick=\"GetTarget("+(j)+");\"style='margin-left: 2px'>" + (j) + "</a></li>");
    //         }else if(j > 0){
    //             pages.append("<li class=\"pagination-item\">" + "<a onclick=\"GetTarget(" + (j) + ")\" style = 'margin-left:2px'>" + (j) + "</a></li>");
    //         }
    //     }
    //     if(next > 5 && next < totalPage){
    //         pages.append("<li class=\"pagination-item\">" + "<a onclick=\"GetTarget(" + (next) + ");\" style = 'margin-left:2px'> next</a></li>");
    //     }
    // }
    return (

        <div className="tablesandPageNums">
            <table id="eventTable">
                <thead>
                    <tr>
                        <th>공연/행사명</th><th>시작일자</th><th>종료일자</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td id="tdName0"></td><td id="tdStart0"></td><td id="tdEnd0"></td>
                    </tr>
                    <tr>
                        <td id="tdName1"></td><td id="tdStart1"></td><td id="tdEnd1"></td>
                    </tr>
                    <tr>
                        <td id="tdName2"></td><td id="tdStart2"></td><td id="tdEnd2"></td>
                    </tr>
                    <tr>
                        <td id="tdName3"></td><td id="tdStart3"></td><td id="tdEnd3"></td>
                    </tr>
                    <tr>
                        <td id="tdName4"></td><td id="tdStart4"></td><td id="tdEnd4"></td>
                    </tr>
                    <tr>
                        <td id="tdName5"></td><td id="tdStart5"></td><td id="tdEnd5"></td>
                    </tr>
                    <tr>
                        <td id="tdName6"></td><td id="tdStart6"></td><td id="tdEnd6"></td>
                    </tr>
                    <tr>
                        <td id="tdName7"></td><td id="tdStart7"></td><td id="tdEnd7"></td>
                    </tr>
                    <tr>
                        <td id="tdName8"></td><td id="tdStart8"></td><td id="tdEnd8"></td>
                    </tr>
                    <tr>
                        <td id="tdName9"></td><td id="tdStart9"></td><td id="tdEnd9"></td>
                    </tr>

                </tbody>
            </table>
            <div id = "pagebtns">
                <button id = "buttonFirst">처음으로</button>
                {
                    function chkbtns() {
                        if(currentPage > 10){
                            return(<button>이전</button>);
                        }
                    }
                }
                <button id = "button0">1</button>
                <button id = "button1">2</button>
                <button id = "button2">3</button>
                <button id = "button3">4</button>
                <button id = "button4">5</button>
                <button id = "button5">6</button>
                <button id = "button6">7</button>
                <button id = "button7">8</button>
                <button id = "button8">9</button>
                <button id = "button9">10</button>
                <button>다음</button>
                <button>마지막으로</button>
            </div>
            
            

        </div>

    );
}

export default eventTable;
