//pages/Nav.js
import React from "react";
import './tripNearby.css';
import axios from "axios";



function TripNearby() {
    let currentPage = 1;
    let first = 1;
    let last = 5;
    let totalPage= 5;
    getPageNum();
    getPage();

    //총 페이지 가져오기
    function getPageNum() {
        axios.get('http://210.115.227.82:18080/api/trip/list/totalpage?cnt=8&area=')
            .then(function (response) {
                totalPage = response.data.response.totalPage;
            })
            .catch(function (error) {
                console.log(error);
            })
    }
    
    function PrintDataList(data) {
        
        if(data!=null){
            for (let i = 0; i < 10; i++) {
                document.getElementById("tPlace" + i).innerHTML = data.list[i].title;
                console.log(i);
                
            }
            paging(totalPage, currentPage);
        }
        
        
    }
    
    function getPage() {
        axios.get('http://210.115.227.82:18080/api/trip/list?cnt=8&num='+currentPage+'&area=')
            .then(function (response) {
                console.log(response);
                PrintDataList(response.data.response);
            })
            .catch(function (error) {
                //console.log(error);
            })
    }

    function getPage() {
        let type;
        if (isEvent) {
            //console.log("true");
            type = "event";
        } else {
            type = "festival";
        }
        axios.get('http://210.115.227.82:18080/api/' + type + '/list?cnt=10&num=' + currentPage)
            .then(function (response) {
                responseData = response.data.response;
                //console.log(responseData);
                PrintDataList(response.data.response);
            })
            .catch(function (error) {
                //console.log(error);
            })
    }


    //페이징 버튼 구현
    function btn1_click() {
        console.log((first) + "clicked!");
        currentPage = first;
        getPage();
    }
    function btn2_click() {
        //console.log((first + 1) + "clicked!");
        currentPage = first + 1;
        getPage();
    }
    function btn3_click() {
        //console.log((first + 2) + "clicked!");
        currentPage = first + 2;
        getPage();
    }
    function btn4_click() {
        //console.log((first + 3) + "clicked!");
        currentPage = first + 3;
        getPage();
    }
    function btn5_click() {
        currentPage = first + 4;
        getPage();
    }

    function paging(totalPage, currentPage) {
        console.log("toto " + totalPage);
        last = 5;
        if (currentPage < 4) {
            last = 5;
        } else if (currentPage > totalPage - 2) {
            last = totalPage;
        } else {
            last = currentPage + 2;
        }
        first = last - 4;

        
        console.log("first = " + first);                                          
        for (let i = 1; i < 10; i = i + 1 ) {
            document.getElementById("btn" + i).innerHTML = (first + i - 1);

        }
    }


    
    return (
        <div className="Tripcontents">
            <span id = "quote">#내 주변 여행지</span><hr/>
            <div id = "row1">
                <div id = "tPlace0"></div>
                <div id = "tPlace1"></div>
                <div id = "tPlace2"></div>
                <div id = "tPlace3"></div>
            </div>
            <div id = "row2">
            <div id = "tPlace4"></div>
            <div id = "tPlace5"></div>
            <div id = "tPlace6"></div>
            <div id = "tPlace7"></div>
            </div>
            
            <div id="pagebtnsinTripComp">
                <button id="btn1" onClick={btn1_click}></button>
                <button id="btn2" onClick={btn2_click}></button>
                <button id="btn3" onClick={btn3_click}></button>
                <button id="btn4" onClick={btn4_click}></button>
                <button id="btn5" onClick={btn5_click}></button>
            </div>
        </div>
    );
}

export default TripNearby;
