//pages/Nav.js
import React from "react";
import axios from "axios";
import HeaderMain from "../header/headerMain";
import "./eventTable.css";



function eventTable() {

    function EventCheck(){
        let btnEvent = document.getElementById('selectEvent');
        let btnFest = document.getElementById('selectFest');
        btnEst.disabled = true;
        btnWst.disabled = false;
        
    }
    //영서 클릭 시 함수
    function FestivalCheck() {
        let btnEvent = document.getElementById('selectEvent');
        let btnFest = document.getElementById('selectFest');
        btnEst.disabled = false;
        btnWst.disabled = true;
        
    }


    eventTable.defaultProps = {
        name : true
      };

    let currentPage = 1;
    let totalPage;
    let first;
    let last;
    getPage();

    //페이지 가져오기. api에서 필요한 데이터를 페이지로 가져옴
    function getPage() {
        axios.get('http://210.115.227.82:18080/api/event/list?cnt=10&num=' + currentPage)
        .then(function (response) {
            //console.log(response.data.response.list[0]);
            PrintDataList(response.data.response);
        })
        .catch(function (error) {
            console.log(error);
        })
    }

    //페이징 버튼 구현
    function btn1_click() {
        console.log((first) + "clicked!");
        currentPage = first;
        getPage();
    }
    function btn2_click() {
        console.log((first+1) + "clicked!");
        currentPage = first+1;
        getPage();
    }
    function btn3_click() {
        console.log((first+2) + "clicked!");
        currentPage = first+2;
        getPage();
    }
    function btn4_click() {
        console.log((first+3) + "clicked!");
        currentPage = first+3;
        getPage();
    }
    function btn5_click() {
        console.log((first+4) + "clicked!");
        currentPage = first+4;
        getPage();
    }


    //data 화면에 출력
    function PrintDataList(data) {
        for (let i = 0; i < 10; i++) {
            document.getElementById("tdName" + i).innerHTML = data.list[i].title;
            document.getElementById("tdStart" + i).innerHTML = data.list[i].startDate;
            document.getElementById("tdEnd" + i).innerHTML = data.list[i].endDate;
        }
        paging(totalPage,currentPage);
    }
    //총 페이지를 가져오는 데이터
    axios.get('http://210.115.227.82:18080/api/event/list/totalpage')
        .then(function (response) {
            //console.log(response.data.response.list[0]);
            console.log(response.data.response);
            totalPage = response.data.response.count;

        })
        .catch(function (error) {
            console.log(error);
        })


    function paging(totalPage,currentPage) {
        const pageGroup = Math.ceil(currentPage / 5);
        //console.log("totalpage: " + totalPage);

        //set last page num;
        last = 5;
        if(currentPage <4){
            last = 5;
        }else if(currentPage > totalPage-2){
            last = totalPage;
        }else{
            last = currentPage + 2;
        }
        first = last - 4;


        console.log("first = " + first);
        for(let i = 1 ; i < 6 ; i++){
            document.getElementById("btn" + i).innerHTML = (first+i-1);
        }
    }

    return (

        <div className="tablesandPageNums">
            <button id="selectEvent"> 공연 행사 </button>
            <button id="selectFest"> 문화 축제 </button>
            <table id="eventTable">
                <thead>
                    <tr>
                        <th>공연/행사명</th><th>시작일자</th><th>종료일자</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td id="tdName0"></td><td id="tdStart0"></td><td id="tdEnd0"></td>
                    </tr>
                    <tr>
                        <td id="tdName1"></td><td id="tdStart1"></td><td id="tdEnd1"></td>
                    </tr>
                    <tr>
                        <td id="tdName2"></td><td id="tdStart2"></td><td id="tdEnd2"></td>
                    </tr>
                    <tr>
                        <td id="tdName3"></td><td id="tdStart3"></td><td id="tdEnd3"></td>
                    </tr>
                    <tr>
                        <td id="tdName4"></td><td id="tdStart4"></td><td id="tdEnd4"></td>
                    </tr>
                    <tr>
                        <td id="tdName5"></td><td id="tdStart5"></td><td id="tdEnd5"></td>
                    </tr>
                    <tr>
                        <td id="tdName6"></td><td id="tdStart6"></td><td id="tdEnd6"></td>
                    </tr>
                    <tr>
                        <td id="tdName7"></td><td id="tdStart7"></td><td id="tdEnd7"></td>
                    </tr>
                    <tr>
                        <td id="tdName8"></td><td id="tdStart8"></td><td id="tdEnd8"></td>
                    </tr>
                    <tr>
                        <td id="tdName9"></td><td id="tdStart9"></td><td id="tdEnd9"></td>
                    </tr>

                </tbody>
            </table>
            <div id = "pagebtns">
                <button id = "btn1" onClick = {btn1_click}></button>
                <button id = "btn2" onClick = {btn2_click}></button>
                <button id = "btn3" onClick = {btn3_click}></button>
                <button id = "btn4" onClick = {btn4_click}></button>
                <button id = "btn5" onClick = {btn5_click}></button>
            </div>



        </div>

    );
}

export default eventTable;
