import axios from "axios";
import React from 'react';
import './weatherCast.css';

// 
function WeatherCast() {
    //일단 보여줌.
    showWeather('?regId=11D10401' , '?regId=11D20000');
    /*
        중기기온조회 / 중기육상예보조회 공통
        영동 / 영서로 나누어 데이터를 보여주는 부분
    */

    //영동 클릭 시 함수
    function EastCheck(){
        let btnEst = document.getElementById('east');
        let btnWst = document.getElementById('west');
        btnEst.disabled = true;
        btnWst.disabled = false;
        showWeather('?regId=11D10401' , '?regId=11D20000');
    }
    //영서 클릭 시 함수
    function WestCheck() {
        let btnEst = document.getElementById('east');
        let btnWst = document.getElementById('west');
        btnEst.disabled = false;
        btnWst.disabled = true;
        showWeather('?regId=11D10301' , '?regId=11D10000');
    }

    //클릭 후 날씨 출력부분
    function showWeather(regId1, regId2) {
        let p3txt,p4txt,p5txt,p6txt,p7txt,p8txt,p9txt,p10txt;
        
        //중기 기온 조회
        axios.get('http://210.115.227.82:18080/api/weather/midta' + regId1)
            .then(function (response) {
                let taMin = response.data.response.item[0];
                document.getElementById("temp3").innerHTML = taMin.taMin3 + "° / " + taMin.taMax3 + "°";
                document.getElementById("temp4").innerHTML = taMin.taMin4 + "° / " + taMin.taMax4 + "°";
                document.getElementById("temp5").innerHTML = taMin.taMin5 + "° / " + taMin.taMax5 + "°";
                document.getElementById("temp6").innerHTML = taMin.taMin6 + "° / " + taMin.taMax6 + "°";
                document.getElementById("temp7").innerHTML = taMin.taMin7 + "° / " + taMin.taMax7 + "°";
                document.getElementById("temp8").innerHTML = taMin.taMin8 + "° / " + taMin.taMax8 + "°";
                document.getElementById("temp9").innerHTML = taMin.taMin9 + "° / " + taMin.taMax9 + "°";
                document.getElementById("temp10").innerHTML = taMin.taMin10 + "° / " + taMin.taMax10 + "°";
            })
            .catch(function (error) {
                console.log(error);
            })
        //중기 육상예보조회
        
        axios.get('http://210.115.227.82:18080/api/weather/midlandfcst' + regId2)
            .then(function (response){
                let rainRate = response.data.response.item[0];

                
/*
                p3txt += "3일뒤 오전 강수 확률 : " + response.data.response.item[0].rnSt3Am + "% / ";
                p4txt += "4일뒤 오전 강수 확률 : " + response.data.response.item[0].rnSt4Am + "% / ";
                p5txt += "5일뒤 오전 강수 확률 : " + response.data.response.item[0].rnSt5Am + "% / ";
                p6txt += "6일뒤 오전 강수 확률 : " + response.data.response.item[0].rnSt6Am + "% / ";
                p7txt += "7일뒤 오전 강수 확률 : " + response.data.response.item[0].rnSt7Am + "% / ";
                p8txt += "8일뒤 오전 강수 확률 : " + response.data.response.item[0].rnSt8 + "% / ";
                p9txt += "9일뒤 오전 강수 확률 : " + response.data.response.item[0].rnSt9 + "% / ";
                p10txt += "10일뒤 오전 강수 확률 : " + response.data.response.item[0].rnSt10 + "% / ";

                p3txt += "3일뒤 오후 강수 확률 : " + response.data.response.item[0].rnSt3Pm + "% / ";
                p4txt += "4일뒤 오후 강수 확률 : " + response.data.response.item[0].rnSt4Pm + "% / ";
                p5txt += "5일뒤 오후 강수 확률 : " + response.data.response.item[0].rnSt5Pm + "% / ";
                p6txt += "6일뒤 오후 강수 확률 : " + response.data.response.item[0].rnSt6Pm + "% / ";
                p7txt += "7일뒤 오후 강수 확률 : " + response.data.response.item[0].rnSt7Pm + "% / ";
                p8txt += "8일뒤 오후 강수 확률 : " + response.data.response.item[0].rnSt8 + "% / ";
                p9txt += "9일뒤 오후 강수 확률 : " + response.data.response.item[0].rnSt9 + "% / ";
                p10txt += "10일뒤 오후 강수 확률 : " + response.data.response.item[0].rnSt10 + "% / ";
                


                document.getElementById("plus3").innerHTML = p3txt;
                document.getElementById("plus4").innerHTML = p4txt;
                document.getElementById("plus5").innerHTML = p5txt;
                document.getElementById("plus6").innerHTML = p6txt;
                document.getElementById("plus7").innerHTML = p7txt;
                document.getElementById("plus8").innerHTML = p8txt;
                document.getElementById("plus9").innerHTML = p9txt;
                document.getElementById("plus10").innerHTML = p10txt;*/
                
            })
            .catch(function (error){
                console.log(error);
            })

    }



    /*
        기상청 중기기온조회 부분
        중기기온 조회 >> 오늘 기준 3 - 10 일간 최저 / 최고 기온을 영동 / 영서로 나누어 표현
    */

    //기상청 중기기온조회







    /*
        기상청 중기전망 조회부분
        중기전망 api데이터 수집 및 데이터 글자를 잘라 가공 후 변수로 제공
    */
    //weatherCast 글자 자르기
    function cutCastQuote(str) {
        let data = str.split('○');
        let rain, temp, sea, weekend;
        rain = data[1];
        temp = data[2];
        sea = data[3];
        weekend = data[4];
        document.getElementById('weather').innerHTML = (rain + "<br>" + temp + "<br>" + sea + "<br>" + weekend + "<br>");
    }

    //기상청 중기전망조회
    axios.get('http://210.115.227.82:18080/api/weather/midfcst')
        .then(function (response) {
            let weatherdata = response.data.response.wfSv;
            //글자 자르고 weather div.innerHTml 에 연결
            cutCastQuote(weatherdata);
            //console.log(weatherdata);
        })
        .catch(function (error) {
            console.log(error);
        })

    //필요데이터 > 일단은 글자로 보여줌, 추후 디자이너와 합의 필요.
    return (
        <div id="weatherDIV">
            <button id="east" onClick={EastCheck}> 영동 </button>
