import React, { Component } from "react";
import axios from "axios";


//pages/Nav.js
import { Link } from "react-router-dom";
//import './pageLists.css';


function EventDetail() {
    let type = sessionStorage.getItem("type");
    let title = sessionStorage.getItem("title");
    let eventId = sessionStorage.getItem("eventId");

    axios.get('http://210.115.227.82:18080/api/' + type + '/info?id='+eventId+'&title=' + title)
            .then(function (response) {
                console.log(response.data.response.info);
                document.getElementById("title").innerHTML += response.data.response.info.title;
                document.getElementById("content").innerHTML += response.data.response.info.content;
                document.getElementById("homepage").innerHTML += response.data.response.info.homepage;
                document.getElementById("phoneNumber").innerHTML += response.data.response.info.phoneNumber;
                document.getElementById("startDate").innerHTML += response.data.response.info.startDate;
                document.getElementById("endDate").innerHTML += response.data.response.info.endDate;
            })
            .catch(function (error) {
                //console.log(error);
            })

    
    return (
        <div className="icons">
            <span id = "title"> 제목 : </span> <br/>
            <span id = "content"> 분류 : </span>
            <span id = "homepage"> 홈페이지 주소 : </span>
            <span id = "phoneNumber"> 전화번호 : </span>
            <span id = "startDate"> 시작일 : </span>
            <span id = "endDate"> 종료일 : </span>
            
        </div>
    );
}

export default EventDetail;
